// Map of key (original) -> value (replacement)
var words = [];
// Push map keys onto array.
$.each(map, function(k) { words.push(k); });
// Regex to globally, case-insensitively match words. Note that this simplistic
// regex will treat punctuation like ' or - as word separators, so beware!
var regex = new RegExp("\\b(" + words.join("|") + ")\\b", "g");
// Replacer function.
var replacer = function(value) { return map[value]; };


var message = document.getElementById("message");
var displayMessage = document.getElementById("displayMessage");
var correct = document.getElementById("result-correct");
var incorrect = document.getElementById("result-incorrect");
var streakIncorrect = document.getElementById("streak-incorrect");
var streakCorrect = document.getElementById("streak-correct");
var correctAnswer;
var streak = 0;

const newMessage = async () => {
    message.style.opacity = "0";
    const response = await fetch('https://ai-guess-aba1397668ce.herokuapp.com/api/', {
        method: 'GET',
        headers: {
        'Content-Type': 'application/json'
        }
    });
    const myJson = await response.json(); //extract JSON from the http response
    if(myJson === undefined || myJson.result === "" || myJson.string === undefined) {
        newMessage();
    }
    correctAnswer = myJson.result;
    var generatedMessage = myJson.string;
    generatedMessage = generatedMessage.replace(":)", "<img alt='\:-?\)' title='\:-?\)' class='twitch-emote twitch-emote-0' src='https://static-cdn.jtvnw.net/emoticons/v1/1/3.0'>");
    generatedMessage = generatedMessage.replace("D:", "<img alt='D:' title='D:' class='twitch-emote twitch-emote-0' src='https://cdn.betterttv.net/emote/55028cd2135896936880fdd7/3x'>");
    generatedMessage = generatedMessage.replace(regex, replacer);
    displayMessage.innerHTML = generatedMessage;
    setTimeout(() => {
        message.style.opacity = "1";
    }, 100);
};

function guess (guess) {
    var result;
    console.log(guess);
    if(guess === correctAnswer) {
        result = correct;
    } else {
        result = incorrect;
    }
    result.style.transform = "scale(1)";
    message.style.pointerEvents = "none";
    result.style.transition = "transform 0.5s cubic-bezier(0.175, 0.885, 0.32, 1.275)";
    if(result === correct) {
        newMessage();
        streak++;
        streakCorrect.innerHTML = "Current Streak: " + streak;
        setTimeout(() => {
            result.style.transform = "scale(0)";
            // message.style.opacity = "1";
            message.style.pointerEvents = "revert";
            setTimeout(() => {
                result.style.transition = "none";
            }, 200);
        }, 500);
    } else {
        message.style.opacity = "0";
        streakIncorrect.innerHTML = "Streak: " + streak;
    }
}

